glsl3.vert:
precision highp float;
uniform CCGlobal {
  mat4 cc_matView;
  mat4 cc_matViewInv;
  mat4 cc_matProj;
  mat4 cc_matProjInv;
  mat4 cc_matViewProj;
  mat4 cc_matViewProjInv;
  vec4 cc_cameraPos;
  vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_screenScale;
};
in vec3 a_position;
in vec4 a_color;
in vec2 a_uv0;
out vec4 v_color;
out vec2 v_uv0;
void main () {
  gl_Position = cc_matViewProj * vec4(a_position, 1);
  v_color = a_color;
  v_uv0 = a_uv0;
}

glsl3.frag:
precision highp float;
uniform CCGlobal {
  mat4 cc_matView;
  mat4 cc_matViewInv;
  mat4 cc_matProj;
  mat4 cc_matProjInv;
  mat4 cc_matViewProj;
  mat4 cc_matViewProjInv;
  vec4 cc_cameraPos;
  vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_screenScale;
};
in vec4 v_color;
in vec2 v_uv0;
uniform sampler2D texture;
uniform Properties {
  float amplitude;
  float angularVelocity;
  float frequency;
  float offset;
  bool toLeft;
};
void main () {
  vec4 color = v_color;
  color *= texture(texture, v_uv0);
  if(color.a == 0.0) discard;
  float y = amplitude * sin((angularVelocity * v_uv0.x) + ((frequency * cc_time.x) * (toLeft ? 1. : -1.))) + offset;
  if(v_uv0.y < y) discard;
  gl_FragColor = color;
}

